import { Button, Form, Input } from "antd";

const Signup = () => {
  return (
    <>
      <Form
        name="basic"
        labelCol={{
          span: 8,
        }}
        wrapperCol={{
          span: 16,
        }}
        style={{
          maxWidth: 600,
          border: "1px solid #80808069",
          margin: "auto",
          padding: "40px",
          backgroundColor: "azure",
        }}
      >
        <Form.Item
          label="User Name or Email"
          name="username"
          rules={[
            {
              required: true,
              message: "please enter your valid user name",
            },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="Password"
          name="password"
          rules={[
            {
              required: true,
              message: "Please input your password!",
            },
          ]}
        >
          <Input.Password />
        </Form.Item>

        <Form.Item
         wrapperCol={{
            offset: 8,
            span: 16,
          }}
          style={{Display:"flex",justifyContent:"space-between",border:"2px solid red"}}
        >
          <Button
             type="primary"
             htmlType="submit"
            >
            SignUp
          </Button>
          <Button 
            htmlType="button"
          >
            Reset
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default Signup;
