import { Button, Form, Input, Radio } from "antd";

const Signup = () => {
    const [form] = Form.useForm();

    const onReset = () =>{
        form.resetFields();
    }

  return (
    <div className="signup_wrapper">

    <h1 className="form_title">Become A Member</h1>

      <Form
        name="basic"
        labelCol={{
          span: 8,
        }}
        wrapperCol={{
          span: 16,
        }}
        style={{
          maxWidth: 600,
          border: "1px solid #80808069",
          margin: "auto",
          padding: "40px",
          backgroundColor: "azure",
        }}
      >
        <Form.Item
          label="First Name"
          name="firstname"
          rules={[
            {
              required: true,
              message: "please enter your first name",
            },
          ]}
        >
          <Input placeholder="enter your first name"/>
        </Form.Item>
        <Form.Item
          label="last Name"
          name="lastname"
          rules={[
            {
              required: true,
              message: "please enter your last name",
            },
          ]}
        >
          <Input placeholder="enter your last name"/>
        </Form.Item>
        <Form.Item label="Gender" className="signup_gender">
          <Radio.Group>
            <Radio value="male"> Male </Radio>
            <Radio value="female"> Female </Radio>
            <Radio value="othe"> Other </Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item
          label="Password"
          name="password"
          rules={[
            {
              required: true,
              message: "Please enter your password!",
            },
          ]}
          tooltip="password should be atleast 6 charecters"
        >
          <Input.Password />
        </Form.Item>
        <Form.Item
          label="Confirm Password"
          name="confirmpassword"
          rules={[
            {
              required: true,
              message: "Please enter your password again!",
            },
          ]}
          tooltip="password should be same"
        >
          <Input.Password />
        </Form.Item>

        <Form.Item
         wrapperCol={{
            offset: 8,
            span: 16,
          }}
          className="signup_button"
        >
          <Button
             type="primary"
             htmlType="submit"
            >
            SignUp
          </Button>
          <Button
            htmlType="button"
            onClick={onReset}
          >
            Reset
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default Signup;
